INSERT INTO `quest_template_locale` VALUES (8741, 'frFR', 'Le retour du champion', 'Le gardien attend votre retour, $n. Apportez le fragment de sceptre vert à Anachronos, dans les Grottes du temps.', 'Apporter le Fragment de sceptre vert à Anachronos dans les Grottes du temps de Tanaris.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8741, 'ruRU', 'Возвращение победителя', 'Хранитель ждет твоего возвращения, $n. Отнеси этот осколок зеленого скипетра Анахроносу в Пещеры Времени.', 'Отнесите осколок зеленого скипетра Анахроносу в Пещеры Времени в Танарисе.', '', '', '', '', '', '', 18019);
