INSERT INTO `quest_template_locale` VALUES (8915, 'ruRU', 'Серьезное предложение', 'Привет тебе, |3-6($c)! Судя по тому, как ты держишься, тебе немало довелось повидать на белом свете. Но бьюсь об заклад, что таких доспехов, как эти, тебе видеть не доводилось!$b$bОкажи мне небольшую услугу, и я отдам их тебе в обмен на обычные поручья.$b$bМне нужен яд тварей, обитающих в Силитусе. Принеси мне этот яд и немного золотых монет, и я произведу обмен.', 'Добудьте 15 порций яда силитида и 20 золотых и принесите их вместе с наручниками Магистра Моквару в Оргриммар.', '', 'Вернитесь к Моквару в Оргриммар.', '', '', '', '', 18019);
