INSERT INTO `quest_template_locale` VALUES (8977, 'deDE', 'Rückkehr zu Deliana', 'War mir eine Freude, mit Euch zusammenzuarbeiten, $n. Hier ist das fertige Produkt.$B$BIch hoffe, dass es Euch bei Eurem Unterfangen behilflich sein wird. Verbreitet doch bitte die Kunde über meine hervorragenden Dienste, wenn es Euch nichts ausmacht. Ich hätte nichts gegen mehr Kundschaft einzuwenden.', 'Bringt Deliana in Eisenschmiede den extradimensionalen Geisterdetektor.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8977, 'esES', 'Regresa junto a Deliana', 'Me ha gustado trabajar contigo, $n. Aquí tienes el producto acabado.$B$BEspero que te ayude en tu misión Y si no te importa, hazme un poco de publicidad. ¡Me iría bien aumentar la clientela!', 'Lleva el detector de fantasmas extra dimensional a Deliana a Forjaz.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8977, 'esMX', 'Regresa junto a Deliana', 'Me ha gustado trabajar contigo, $n. Aquí tienes el producto acabado.$B$BEspero que te ayude en tu misión Y si no te importa, hazme un poco de publicidad. ¡Me iría bien aumentar la clientela!', 'Lleva el detector de fantasmas extra dimensional a Deliana a Forjaz.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8977, 'frFR', 'Retourner voir Deliana', 'Travailler avec vous était très agréable, $n. Voici le produit fini.$B$BJ’espère qu’il vous rendra service dans vos activités présentes. Oh… Et si cela ne vous dérange pas trop, parlez de moi autour de vous. J’aimerais bien avoir davantage de clients.', 'Apportez le Révélateur de fantômes extradimensionnel à Deliana à Forgefer.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8977, 'ruRU', 'Возвращение к Делиане', 'С тобой здорово сотрудничать, $n. Вот готовое изделие.$B$BНадеюсь, оно поможет тебе в твоих дальнейших подвигах. Да, и если ты не против, рассказывай всем обо мне. Мне лишние клиенты не помешают!', 'Принесите спектральный сканер иных измерений Делиане в Стальгорн.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8977, 'zhCN', '向德莉亚娜回复', '跟你合作真是愉快，$N。这是你要的成品。$B$B希望它能助你一臂之力。呃，如果你不介意的话，请帮我宣传宣传。我希望有更多的顾客！', '将超维度幽灵显形器交给铁炉堡的德莉亚娜。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (8978, 'deDE', 'Rückkehr zu Mokvar', 'War mir eine Freude, mit Euch zusammenzuarbeiten, $n. Hier ist das fertige Produkt.$B$BIch hoffe, dass es Euch bei Eurem Unterfangen behilflich sein wird. Verbreitet doch bitte die Kunde über meine hervorragenden Dienste, wenn es Euch nichts ausmacht. Ich hätte nichts gegen mehr Kundschaft einzuwenden.', 'Kehrt mit dem extradimensionalen Geisterdetektor zu Mokvar in Orgrimmar zurück.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (8978, 'esES', 'Regresa junto a Mokvar', 'Me ha gustado trabajar contigo, $n. Aquí tienes el producto acabado.$B$BEspero que te ayude en tu misión Y si no te importa, hazme un poco de publicidad. ¡Me iría bien aumentar la clientela!', 'Lleva el detector de fantasmas extradimensional a Mokvar a Orgrimmar.', '', '', '', '', '', '', 18019);
