INSERT INTO `quest_template_locale` VALUES (9036, 'ruRU', 'Ножные латы неустрашимости', 'Ты ведь отправляешься на войну с Плетью в Наксрамас? Если тебе попадутся испоганенные доспехи и обломки доспехов, принеси их мне, я сделаю тебе броню, достойную величайших героев.', 'Корфакс из Часовни Последней Надежды, что в Восточных Чумных землях, готов сделать ножные латы неустрашимости, если вы принесете ему следующее: 1 испоганенные ножные латы, 20 обломков доспехов, 4 слитка арканита и 3 обработанных грубых шкуры.', '', 'Вернитесь к Корфаксу, Воителю Света, в Восточные Чумные земли.', '', '', '', '', 18019);
