INSERT INTO `quest_template_locale` VALUES (9196, 'itIT', 'Craftsman\'s Writ - Thorium Grenade', 'Deliver 20 Thorium Grenades and the Craftsman\'s Writ - Thorium Grenade - to Packmaster Stonebruiser at Light\'s Hope Chapel in the Eastern Plaguelands.', 'The bearer of this writ is entitled to payment in the form of an insignia of their choice upon completion of the contracted work order.$B$BWork Order EN-7:$B$B20 Thorium Grenades.$B$BAll filled orders should be delivered to Packmaster Stonebruiser at Light\'s Hope Chapel in the Eastern Plaguelands.$B$BContract is null and void if tampered with or damaged.$B$B-Dispatch Commander Metz, The Argent Dawn', '', '', '', '', '', '', 0);
