INSERT INTO `quest_template_locale` VALUES (9235, 'ruRU', 'Наручи ледяной погибели', 'Благодаря тебе и Омариону – да будет земля ему пухом – я могу создать наручи ледяной погибели. От тебя требуются лишь материалы и скромная плата за работу.', 'Принесите 4 морозных руны, 12 ториевых слитков, 2 арканитовых слитка, 2 субстанции Воды и 200 золотых ремесленнику Вильгельму в Часовню Последней Надежды в Восточные Чумные земли.', '', 'Вернитесь к ремесленнику Вильгельму в Восточную башню, что в Восточных Чумных землях.', '', '', '', '', 18019);
