INSERT INTO `quest_template_locale` VALUES (9316, 'frFR', '[TXT] Sauvages gobelins', 'Brianna Schneider sur l\'île des concepteurs veut 10 Dents de gobelins.', '<Brianna vous observe attentivement.>$B$BAlors comme ça vous voulez tuer des gobelins ? Bon, si vous y tenez, vous pouvez commencer avec les sauvages des alentours. Apportez-moi leurs dents pour preuves de vos exploits.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (9316, 'itIT', '[TXT] Goblin Savages', 'Brianna Schneider on Designer Island wants 10 Goblin Teeth.', '<Brianna gives you a searching glance.>$B$BSo, you want to kill some goblins, eh? Well, if you\'re determined, you can start with the nearby savages. Bring me their teeth as proof of your deeds.', '', '', '', '', '', '', 0);
