INSERT INTO `quest_template_locale` VALUES (9316, 'ruRU', '[TXT] Гоблины-варвары', 'Брианна Шнайдер с острова Дизайнера хочет 10 гоблинских зубов.', '*Брианна испытующе смотрит на вас.*$B$BЗначит, вы желаете поохотиться на гоблинов? Что же, если вы решили, то можете начать с варваров, что живут по соседству. А в знак доказательства вашей победы принесите мне их зубы.', '', '', '', '', '', 'Вернитесь к Bri\'s Test Character на Остров Дизайнера.', 0);
INSERT INTO `quest_template_locale` VALUES (9316, 'zhCN', '[TXT]哥布林掠夺者', '<布理安娜用搜索的目光看了你一眼。>$B$B$B$B那么，你想杀一些哥布林，是吗？嗯，如果你下定决心，可以从附近的掠夺者开始。帮我带来他们的牙齿做为证据。', '设计者之岛的布理安娜·史奈德要10颗哥布林牙齿。', '', 'Return to Bri s Test Character in Designer Island.', '', '', '', '', 0);
