INSERT INTO `quest_template_locale` VALUES (9326, 'koKR', '언더시티 불꽃 훔치기', '한여름 축제 현자에게 언더시티 불꽃을 가져가야 합니다.', '언더시티 너머의 폐허에서 훔친 이 불꽃은 무한한 따뜻함을 줍니다. 당신에게는 이 자연의 힘이 낯설겠지만 한여름 축제 현자라면 이 불꽃에 대해 뭔가 알고 있을 겁니다.', '', '', '', '', '', '', 0);
