INSERT INTO `quest_template_locale` VALUES (9326, 'ruRU', 'Похищение пламени Подгорода', 'Верните Пламя Подгорода хранителю мудрости Праздника.', 'Это пламя, похищенное из руин над Подгородом, наполняет тебя теплом. Эта сырая энергия чужда тебе, но Хранитель мудрости может знать больше...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (9326, 'zhCN', '盗取幽暗城的烈焰', '将幽暗城烈焰交给节日博学者。', '从幽暗城上面的洛丹伦废墟偷来的烈焰令你感到异常的温暖。你对这种罕见的力量一无所知，但是博学者或许知道些什么……', '', '', '', '', '', '', 0);
