INSERT INTO `quest_template_locale` VALUES (9596, 'frFR', 'Contrôle', '<NYI>Faites un autre truc, $c !', '<NYI>Résumé des objectifs', '', 'Retournez voir Jonathan LeCraft sur l\'Île des concepteurs.', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (9596, 'ruRU', '<DEPRECATED>Контроль', '<NYI>Сделай что-нибудь, |3-6($c)!', '<NYI>Запись в дневнике.', '', 'Вернитесь к Джонатану Лекрафту на Остров Дизайнера.', '', '', '', '', 18019);
