INSERT INTO `quest_template_locale` VALUES (9650, 'frFR', 'Amélioration magique', '<TXT>Oui, améliorons mes capacités magiques. C\'est logique. Après tout, je pratiquais la magie lorsque j\'étais vivante.$B$BLe fait d\'avoir une arme comme réceptacle physique ne vous transforme pas forcément en brute épaisse.$B$BJe vais avoir besoin de vous pour rassembler quelques ingrédients.', '<TXT>', '', '', '', '', '', '', 18019);
