INSERT INTO `quest_template_locale` VALUES (10041, 'ruRU', 'Кто они?', 'Замаскируйтесь и поговорите с Мрачным посвященным, Мрачным работником и Мрачным советником, после чего вернитесь к разведчице Нефтис в деревню Грангол\'вар в лесу Тероккар.', 'Не знаю, кто это, но ведут они себя очень враждебно!$B$BЯ дам вам маскировочный костюм, чтобы вы пошли туда и поговорили с тремя из них, наиболее болтливыми – с посвященным, работником и советником.$B$BНо будьте осторожны – там есть несколько охотников, которые узнают вас даже в камуфляже, а с ними лучше не связываться. И что бы ни произошло, ни в коем случае не покидайте деревни, потому что маскировочный костюм очень непрочен! Если вдруг потеряете его, то приходите, я дам вам новый.', '', '', '', '', '', '', 0);
