INSERT INTO `quest_template_locale` VALUES (10375, 'frFR', 'Les perles de guerre en obsidienne', 'Vous avez fait vos preuves auprès de mon peuple, $n. Il est temps à présent de montrer que vous êtes $gprêt:prête; à rester à nos côtés, même face au plus fort des ennemis.', '', '', 'Retournez voir .', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (10375, 'ruRU', 'Обсидиановое боевое ожерелье', '', 'Ты много $gсделал:сделала; для нашего народа и всегда $gбыл:была; для нас $gверным другом:доброй подругой;, $n. $gГотов:Готова; ли ты еще раз доказать свою преданность и выступить с нами против сильнейшего из врагов?$B$BОгры Запределья', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10375, 'zhCN', '黑曜石作战念珠', '想要证明你对我们的忠诚，就勇敢地跟我们的敌人作战吧。$B$B残忍野蛮的食人魔是塔拉最大的威胁。如果你想证明自己的实力，就把他们脖子上的作战念珠带回来给我。我会将你取得的成就告诉氏族其它成员的。', '', '', '去找。', '', '', '', '', 0);
