INSERT INTO `quest_template_locale` VALUES (10464, 'deDE', 'Das Gelöbnis des Weisen', 'Bringt Euer Band der Ewigkeit zu Soridormi in den Höhlen der Zeit, wenn Ihr einen wohlwollenden Ruf bei den Wächtern der Sande erlangt habt.', 'Ihr habt Euer Versprechen gegeben, $C. Jetzt liegt es an Euch, es einzuhalten.$B$BDient den Wächtern der Sande wohl, und Ihr werdet Euren Lohn erhalten.$B$BWenn Ihr scheitert, wird die Zeitlinie, wie wir sie kennen, zerrissen werden!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10464, 'esES', 'La promesa del sabio', 'Devuelve tu sortija de eternidad a Soridormi en las Cavernas del Tiempo después de alcanzar la reputación de honorable ante La Escama de las Arenas.', 'Ya has hecho tu promesa, $c. Ahora depende de ti cumplirla.$B$BSirve a La Escama de las Arenas y recibirás una recompensa.$B$B¡Fracasa y el discurrir del tiempo tal como lo conocemos se desvanecerá!', '', '', '', '', '', '', 0);
