INSERT INTO `quest_template_locale` VALUES (10870, 'frFR', 'Allié du vol du Néant', 'Permettez à Karynaku de vous ramener auprès de Mordenai dans les Champs de l\'Aile-du-Néant.', 'Permettez-moi de vous ramener auprès de Mordenai. Je suis certaine qu\'il souhaite vous voir encore une fois.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10870, 'itIT', 'Alleato degli Alafatua', 'Permetti a Karynaku di riportarti da Mordenai nei Campi degli Alafatua.', 'Permettimi di riportarti da Mordenai. Sono certa che vorrà rivederti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10870, 'koKR', '황천날개 용군단의 동맹', '카리나쿠의 인도를 받아 황천날개 벌판에 있는 모르데나이에게 가야 합니다.', '모르데나이에게 돌아가라. 분명 다시 그대를 보고 싶어할 것이다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10870, 'ptBR', 'Aliados dos Asa Etérea', 'Deixe Karynakhan levá-$glo:la; até Mordenai nos Campos da Asa Etérea.', 'Deixe-me levar você a Mordenai. Tenho certeza de que ele quer $gvê-lo:vê-la; novamente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10870, 'ruRU', 'Союзник Крыльев Пустоты', 'Позвольте Каринаку отнести вас к Морденаю на Поля Крыльев Пустоты.', 'Позволь мне вернуть тебя к Морденаю. Я уверен, он не откажется снова тебя повидать', '', '', '', '', '', '', 0);
