INSERT INTO `quest_template_locale` VALUES (10891, 'deDE', 'Imperiale Plattenrüstung', 'In Gadgetzan gibt es einen Zwerg namens Derotain Matschnipper der Euch beibringen kann, wie man eine imperiale Brustplatte herstellt. Ihr müsst nur nach Tanaris reisen und ihn suchen. Interesse?', 'Sprecht Derotain Matschnipper in Gadgetzan in Tanaris.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (10891, 'esES', 'Armadura de placas imperiales', 'Hay un enano en Gadgetzan llamado Derotain Sorbelodo que te puede enseñar a fabricar armaduras de placas imperiales. Todo lo que tienes que hacer es ir al Desierto de Tanaris y encontrarle. ¿Te interesa?', 'Habla con Derotain Sorbelodo en Gadgetzan, en Tanaris.|n', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (10891, 'esMX', 'Armadura de placas imperiales', 'Habla con Derotain Sorbelodo en Gadgetzan, en Tanaris.|n', 'Hay un enano en Gadgetzan llamado Derotain Sorbelodo que te puede enseñar a fabricar armaduras de placas imperiales. Todo lo que tienes que hacer es ir al Desierto de Tanaris y encontrarle. ¿Te interesa?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10891, 'frFR', 'L\'armure impériale en plaques', 'Il y a un nain à Gadgetzan qui s’appelle Derotain Siroteboue, et qui pourra vous apprendre à fabriquer l’armure impériale en plaques. Il vous suffit d’aller dans le désert de Tanaris et de le trouver. Ça vous intéresse ?', 'Parlez à Derotain Siroteboue à Gadgetzan, en Tanaris.', '', '', '', '', '', '', 18019);
INSERT INTO `quest_template_locale` VALUES (10891, 'itIT', 'Imperial Plate Armor', 'Speak with Derotain Mudsipper at Gadgetzan in Tanaris.', 'There\'s a dwarf in Gadgetzan by the name of Derotain Mudsipper who\'ll teach you how to make imperial plate armor. All you gotta do is get to the Tanaris desert and find him. Interested?', '', '', '', '', '', '', 0);
