INSERT INTO `quest_template_locale` VALUES (10891, 'ruRU', 'Имперские латы', 'Поговорите с Деротайном Грязеглотом в Прибамбасске, что в Танарисе.', 'Есть в Прибамбасске дворф по имени Деротайн Грязеглот, который научит тебя ковать имперские латы. И всего-то надо добраться до пустыни Танарис и найти его там. Ну, как?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10891, 'zhCN', '君王板甲', '加基森有位名叫德罗泰恩的矮人能向你传授锻造君王板甲的技艺。转至塔纳利斯沙漠找他吧！', '转至塔纳利斯的加基森，与德罗泰恩谈一谈。', '', '', '', '', '', '', 0);
