INSERT INTO `quest_template_locale` VALUES (10931, 'frFR', 'Retour à l’Exodar', 'Parlez à Caedmos à l’Exodar.', 'Votre maître, Caedmos de l’Exodar, a demandé à vous voir. C’est à propos de votre formation, mais je n’en sais pas davantage.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10931, 'itIT', 'Torna a Exodar', 'Parla con Caedmos a Exodar.', 'Il Maestro Caedmos a Exodar ha chiesto di vederti. Riguarda il tuo addestramento, ma non mi ha detto altro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10931, 'koKR', '엑소다르로 돌아가기', '엑소다르에 있는 캐드모스와 대화해야 합니다.', '엑소다르에 있는 당신의 상급자인 캐드모스가 당신에게 만나자는 전갈을 보내왔습니다. 훈련과 관련된 것 같은데 그 이상은 모르겠군요. 어서 그에게 돌아가 보는 게 좋겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10931, 'ptBR', 'Retorno à Exodar', 'Fale com Edmos na Exodar.', 'O mestre Edmos solicita a tua presença na Exodar. O motivo deste encontro diz respeito ao teu treinamento, mas nada sei além disso.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10931, 'ruRU', 'Возвращение в Экзодар', 'Поговорите с Кадмосом из Экзодара.', 'Тебя желает видеть твой учитель из Экзодара, Кадмос. Речь пойдет о твоем обучении, но о чем именно, я не знаю.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10931, 'zhCN', '回到埃索达', '前往埃索达，与凯德莫斯谈一谈。', '你的老师凯德莫斯要你前往埃索达与他会面。似乎跟你的训练课程有关吧，具体什么我就不得而知了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (10931, 'zhTW', '回到艾克索達', '跟艾克索達的希德摩斯談話。', '你在艾克索達的主人希德摩斯想見你。他關切你的訓練進度，除此之外的事情我不太清楚。', '', '', '', '', '', '', 0);
