INSERT INTO `quest_template_locale` VALUES (10967, 'ruRU', 'Возвращение в приют', 'Отведите свою подопечную обратно в приют Шаттрата, вернув эльфийскую детскую свистульку попечительнице сирот Миле в Нижнем Городе.', 'Как жаль! Пора обратно в приют в Шаттрат.$b$bА мне было так хорошо! Ты лучше всех на свете, $n! Теперь ты $gмой старший брат:моя старшая сестра;!$B$b<У Саландрии наворачиваются слезы на глаза.>$b$bЯ буду по тебе очень скучать! Когда я вырасту, я стану такой же, как ты!!!', '', '', '', '', '', 'Вернитесь к попечительнице сирот Миле в Шаттрат.', 0);
