INSERT INTO `quest_template_locale` VALUES (11442, 'koKR', '가을 축제에 오신 걸 환영합니다!', '[PH] 가을 축제 조직위원과 대화하여 공짜 맥주를 받아야 합니다.', '[PH] 아이언포지 외곽에 있는 가을 축제 광장에서 가을 축제 조직위원을 만나 공짜 맥주를 받으십시오.', '', '', '', '', '', '', 0);
