INSERT INTO `quest_template_locale` VALUES (11445, 'zhTW', '[PH]啤酒廣場B', '[PH]找一個啤酒節宣傳員交談，他會在達納蘇斯、埃索達、沙塔斯城或暴風城外的啤酒廣場。取得一個免費的鹹脆餅。', '[PH]在任何一個啤酒廣場和啤酒節宣傳員交談，並取得免費的鹹脆餅。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'deDE', 'Braufest!', 'Sprecht mit dem Organisator des Braufests.', 'Habt Ihr gehört? Es wird Zeit fürs Braufest! Ihr solltet mit dem Organisator des Braufests am Gelände direkt vor Orgrimmar sprechen. Er gibt Euch sicher ein Freibier aus, damit es richtig losgeht!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'esES', '¡Fiesta de la Cerveza!', 'Habla con el organizador de la Fiesta de la Cerveza.', '¿Te has enterado? ¡Ya está aquí la Fiesta de la Cerveza! Deberías hablar con el organizador en el recinto que hay a las afueras de Orgrimmar. ¡Te dará una cerveza gratis para que vayas calentando!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'esMX', '¡Fiesta de la Cerveza!', 'Habla con el organizador de la Fiesta de la Cerveza.', '¿Escuchaste? ¡Es hora de la Fiesta de la Cerveza! Deberías hablar con el organizador en las tierras de las afueras de Orgrimmar. ¡Te dará una cerveza gratis para comenzar la fiesta!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'frFR', 'La fête des Brasseurs !', 'Parlez à l\'Organisateur de la fête des Brasseurs.', 'Vous êtes au courant ? C’est la saison de la fête des Brasseurs ! Vous devriez parler à l’organisateur, juste devant Ogrimmar. Il vous donnera une bière gratuite pour commencer les réjouissances !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'itIT', 'La Festa della Birra!', 'Parla con l\'Organizzatore della Festa della Birra.', 'Hai sentito? È iniziata la Festa della Birra! Dovresti parlare con uno degli organizzatori della Festa della Birra appena fuori da Orgrimmar. Ti darà una birra gratis per iniziare a festeggiare!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'koKR', '가을 축제!', '가을 축제 조직위원과 대화해야 합니다.', '소식 들으셨나요? 가을 축제 기간이 돌아왔어요! 오그리마 바로 바깥에 있는 가을 축제 조직위원과 얘기해 보세요. 축제의 시작을 알리는 무료 맥주를 줄 거예요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'ptBR', 'CervaFest!', 'Fale com o Organizador da CervaFest.', 'Tá sabendo? A CervaFest se aproxima! Fala com o organizador da CervaFest no terreno em frente a Orgrimmar... ele vai ter dar uma cerveja grátis para dar início aos trabalhos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'ruRU', 'Хмельной фестиваль!', 'Поговорите с распорядителем Хмельного фестиваля.', '$gСлышал:Слышала; новости? Хмельной фестиваль на дворе! Поговори с распорядителем Хмельного фестиваля рядом с Оргриммаром – всем новым гостям по бесплатному пиву!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'zhCN', '美酒节！', '与美酒节组织者谈一谈。', '你听说了吗？美酒节就要开幕了！你应该到奥格瑞玛的城外去，和美酒节组织者谈谈——他会免费送你一杯啤酒，一起开始这场庆典！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11446, 'zhTW', '啤酒節!', '和啤酒節管理人交談。', '你聽見了嗎？該來慶祝啤酒節了！你應該到奧格瑪外面的空地，去找啤酒節的負責人談話…他會給你一杯免費啤酒，當作開場！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (11447, 'deDE', 'Willkommen auf dem Braufest!', 'Sprecht mit dem Organisator des Braufests und erhaltet ein kostenloses Bier.', 'Sprecht mit dem Organisator des Braufests beim Braufestplatz außerhalb von Eisenschmiede, um ein kostenloses Bier zu erhalten.', '', '', '', '', '', '', 0);
