INSERT INTO `quest_template_locale` VALUES (11924, 'itIT', 'Afferrare al volo le torce... ancora', 'Afferra 10 torce, poi parla con il Maestro Mangiafuoco.', 'Afferrare al volo le torce ti costringe a essere sempre $gconcentrato:concentrata;. Credi di poter continuare a lungo senza fermarti?$B$BVediamo...', '', '', '', '', '', 'Torna dal Maestro Mangiafuoco.', 0);
INSERT INTO `quest_template_locale` VALUES (11924, 'koKR', '횃불을 더 많이 잡아라!', '횃불 10개를 던졌다 잡고 나서, 불꽃마술의 대가와 대화해야 합니다.', '횃불 잡기 덕분에 정신이 바짝 드신 것 같군요. 더 오랫동안 하실 수도 있을 것 같은데요?$B$B어디 봅시다...', '', '', '', '', '', '대도시에 있는 불꽃마술의 대가에게 가야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (11924, 'ptBR', 'Mais piromalabarismo', 'Pegue 10 tochas, depois fale com o Mestre Engolidor de Fogo.', 'O piromalabarismo exige alta concentração. Quanto tempo será que você consegue manter as tochas no ar?$B$BVamos ver...', '', '', '', '', '', 'Fale novamente com o Mestre Engolidor de Fogo.', 0);
INSERT INTO `quest_template_locale` VALUES (11924, 'ruRU', 'Опять жонглирование факелами', 'Поймайте 10 факелов, затем поговорите с огнеглотателем-виртуозом.', 'При жонглировании факелами определенно приходится быть начеку. Как думаешь, долго продержишься?$B$BПосмотрим-ка...', '', '', '', '', '', 'Вернитесь к огнеглотателю-виртуозу.', 0);
INSERT INTO `quest_template_locale` VALUES (11924, 'zhCN', '继续接火炬', '连续接住火炬10次，然后与吞火大师谈一谈。', '接火炬游戏是不是很好玩？想不想试试看你能否连续接住更多的次数？$B$B来吧……', '连续接住火炬10次。', '', '', '', '', '去找吞火大师。', 0);
