INSERT INTO `quest_template_locale` VALUES (12452, 'frFR', 'zzOLD The Fate of the Ruby Dragonshrine', 'Apportez la Broche de rubis de Dahlia à Ceristrasz, et racontez-lui son destin.', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12452, 'itIT', 'zzOLD The Fate of the Ruby Dragonshrine', 'Bring Dahlia\'s Ruby Broach to Ceristrasz and inform him of her fate.', '', '', '', '', '', '', '', 0);
