INSERT INTO `quest_template_locale` VALUES (12491, 'koKR', '다이어브루의 독한 맥주', '다이어브루의 독한 맥주를 아이언포지 근처의 가을 축제 광장에 있는 이펠코퍼 아이언케그에게 전달해야 합니다.', '코렌 다이어브루의 시체는 이 작은 술통을 꼭 껴안고 있습니다. 악명이 자자한 그 \'독한 맥주\'임이 분명합니다!', '', '', '', '', '', '', 0);
