INSERT INTO `quest_template_locale` VALUES (12491, 'ruRU', 'Худое варево Худовара', 'Отнесите худое варево Худовара Ипфелькоферу Железному Бочонку в лагерь Хмельного фестиваля возле Стальгорна.', 'Даже после смерти Корен Худовар не выпустил из рук маленькую фляжку, прижатую к животу. Должно быть, в ней хранится знаменитое худое варево!', '', '', '', '', '', '', 0);
