INSERT INTO `quest_template_locale` VALUES (12749, 'ruRU', 'Приятный сюрприз', 'Командор Чумопал в Часовне Багрового Пламени поручил вам казнить Игги Темноклыка, узника из Серебряного Рассвета, заключенного в темнице.', 'Расчищая путь к темницам часовни, мы не ожидали найти там ничего удивительного. Но нас ждал приятный сюрприз! Похоже, орден не терял времени зря – тюремные камеры буквально битком набиты узниками из Серебряного Рассвета. Большинство из них были уже мертвы к тому времени, как мы их обнаружили, но некоторые все еще цепляются за свои жалкие жизни.$B$BЯ как раз собирался отправиться туда и казнить оставшихся пленников, но теперь с гордостью могу поручить тебе эту почетную миссию. Есть там один весьма мерзкий тролль, которого тебе будет особенно приятно предать смерти.', '', '', '', '', '', '', 0);
