INSERT INTO `quest_template_locale` VALUES (12768, 'ruRU', 'Распорядитель храма Драконьего Покоя', 'Поговорите с Тариолстразом в храме Драконьего Покоя.', 'Ты $gдоказал:доказала;, что тебе можно доверить все и даже больше.$B$BВозвращайся в храм Драконьего Покоя, $n, и снова поговори с его распорядителем, Тариолстразом.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12768, 'zhCN', '龙眠神殿的执事', '与龙眠神殿的塔里奥斯塔兹谈一谈。', '你已经证明了自己有资格获得更多的信任。$B$B现在立刻返回龙眠神殿去，再一次与塔里奥斯塔兹执事谈谈吧，亲爱的$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12768, 'zhTW', '龍眠神殿管理者', '和龍眠神殿的泰瑞歐斯塔茲交談。', '你已經證明你值得信賴，甚至比信賴更進一步。$B$B你應該回到龍眠神殿並且和管理者，泰瑞歐斯塔茲再談一談，$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'deDE', 'Der Wächter des Wyrmruhtempels', 'Sprecht mit Tariolstrasz im Wyrmruhtempel.', 'Ihr habt mehr als nur Eure Vertrauenswürdigkeit bewiesen.$B$BIhr solltet zum Wyrmruhtempel zurückkehren und erneut mit dem Wächter Tariolstrasz sprechen, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'esES', 'El administrador del Templo del Reposo del Dragón', 'Habla con Tariolstrasz en el Templo del Reposo del Dragón.', 'Has demostrado que eres de confianza y más.$b$bDeberías volver al Templo del Reposo del Dragón y hablar con el administrador, Tariolstrasz, otra vez, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'esMX', 'El administrador del Templo del Reposo del Dragón', 'Habla con Tariolstrasz en el Templo del Reposo del Dragón.', 'Has demostrado que eres de confianza y más.$b$bDeberías volver al Templo del Reposo del Dragón y hablar con el administrador, Tariolstrasz, otra vez, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'frFR', 'Le régisseur du temple du Repos du ver', 'Parler à Tariolstrasz au Temple du Repos du ver.', 'Vous avez prouvé que nous pouvons vous faire confiance.$B$BVous devriez retourner au temple du Repos du ver et parler à nouveau au régisseur Tariolstrasz, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'itIT', 'Il Custode del Tempio della Lega dei Draghi', 'Parla con Tariolstrasz al Tempio della Lega dei Draghi.', 'Ti sei $gdimostrato:dimostrata; una persona fidata, $n.$B$BTorna al Tempio della Lega dei Draghi e parla di nuovo con il custode, Tariolstrasz.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'koKR', '고룡쉼터 사원의 청지기', '고룡쉼터 사원에 있는 타리올스트라즈와 대화해야 합니다.', '그대는 자신이 믿을 만한 사람임을 잘 보여주었습니다.$B$B고룡쉼터 사원으로 돌아가서 청지기 타리올스트라즈와 한 번 더 이야기하세요, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'ptBR', 'O Administrador do Templo do Repouso das Serpes', 'Fale com Tariolstrasz no Templo do Repouso das Serpes.', 'Você provou ser de confiança.$B$BPor favor, $n, volte ao Templo do Repouso das Serpes e fale novamente com o administrador, Tariolstrasz.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (12769, 'ruRU', 'Распорядитель храма Драконьего Покоя', 'Поговорите с Тариолстразом в храме Драконьего Покоя.', 'Ты $gдоказал:доказала;, что тебе можно доверить все и даже больше.$B$BВозвращайся в храм Драконьего Покоя, $n, и снова поговори с его распорядителем, Тариолстразом.', '', '', '', '', '', '', 0);
