INSERT INTO `quest_template_locale` VALUES (13177, 'koKR', '무자비한 자들에겐 자비도 없다', '겨울손아귀 요새에 있는 사령관 자네스가 호드의 일원 10명을 처치해 달라고 했습니다.', '겨울손아귀 요새에 온 걸 환영하오. 당신의 임무는 간단하오. 바로 호드 녀석들 10명을 처치하는 일이라오. 자, 이제 가시오! 얼라이언스에 승리를 안겨 주시오!', '', '', '', '', '', '', 0);
