INSERT INTO `quest_template_locale` VALUES (13326, 'frFR', 'Suite de Nobles de Sombrelune', 'Rapporter la suite de Nobles à la foire de Sombrelune quand elle reviendra en ville.', 'Maintenant que vous avez rassemblé toutes les cartes de la suite de Nobles, une neuvième carte apparaît mystérieusement, avec le même dos que les huit premières. Cette carte porte l’inscription « Rapportez ces cartes à la foire de Sombrelune, pour recevoir une récompense. » Vous avez entendu parler de cette foire itinérante, qui s’arrête en Mulgore et à Comté-de-l’Or tous les mois.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (13326, 'itIT', 'Mazzo dei Nobili di Lunacupa', 'Consegna il Mazzo dei Nobili alla Fiera di Lunacupa quando arriva in città.', '<Dopo aver raccolto tutte le carte del Mazzo dei Nobili, appare misteriosamente una nona carta che ha lo stesso dorso delle prime otto. Sopra è riportata la scritta: \"Consegna queste carte alla Fiera di Lunacupa e riceverai una ricompensa\". Hai sentito dire che l\'ingresso a questa fiera compare ogni mese a Mulgore e a Borgodoro.>', '', '', '', '', '', '', 0);
