INSERT INTO `quest_template_locale` VALUES (13502, 'deDE', 'Einen Korb bekommen!', 'Sammelt 10 Nobelgartenschokoladen und bringt sie zusammen mit dem geliehenen Eierkorb zurück.', 'Hier... nehmt diesen Eierkorb und macht Euch auf die Suche nach bunt gefärbten Eiern. Kehrt mit dem Korb zu mir zurück, sobald Ihr zehn Stücke Nobelgartenschokolade gesammelt habt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (13502, 'esES', '¿Qué llevas en la cestita?', 'Reúne 10 chocolates del Jardín Noble y devuélvelos junto con la cesta de huevos prestada.', 'Ten... Toma esta cesta de huevos y vete a buscar huevos de colores vivos. Ven a verme con la cesta cuando hayas encontrado 10 chocolates del Jardín Noble.', '', '', '', '', '', '', 0);
