INSERT INTO `quest_template_locale` VALUES (13693, 'ruRU', 'Искатель славы из Сен\'джина', 'Если вы хотите стать искателем славы Сен\'джина, поговорите с Зул\'тором в Павильоне Похитителей Солнца на Ристалище Серебряного Турнира.', 'Ты достойно $gсражался:сражалась; под знаменами Сен\'джина, $n. Теперь тебе следует предстать перед Зул\'тором, предводителем воинов и абсолютным чемпионом Сен\'джина. Он включит тебя в состав участников соревнования, будет наблюдать за твоими тренировками и испытывать на прочность.$B$BДля меня было честью работать с тобой, $n. Удачи тебе на состязаниях!', '', '', '', '', '', '', 0);
