INSERT INTO `quest_template_locale` VALUES (24794, 'deDE', 'Sprecht mit Spraggel', 'Sprecht mit Spraggel Frock in Marschalls Wehr.', 'Meine gute Freundin Spraggel ist schon eine ganze Weile wegen einer gewissen Sache beunruhigt. Ich würde ihr ja helfen, aber wie Ihr seht, habe ich es gerade schön bequem.$B$BKönntet Ihr nicht nachsehen, was sie möchte?', '', '', '', '', '', 'Sprecht mit Spraggel Frock in Marschalls Wehr.', 0);
