INSERT INTO `quest_template_locale` VALUES (24804, 'deDE', 'Ungewöhnliche Düfte', 'Meldet Euch bei Inspektor Snip Schnappbolz in Sturmwind.', 'Irgendetwas stimmt nicht mit dieser Chemiemanufaktur Krone. Dampfdruck hat ein Auge darauf geworfen, aber wir könnten noch ein paar mehr gebrauchen.$B$BBegebt Euch nach Sturmwind, wenn Ihr uns helfen wollt. Inspektor Snip Schnappbolz leitet die Untersuchung dort und er kann jede Hilfe gebrauchen, die er kriegen kann.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (24804, 'esES', 'Olores extraños', 'Preséntate ante el inspector Snip Tuerceganzúas en Ventormenta.', 'Hay gato encerrado en Químicos La Corona, S.L. Los de Bonvapor le tienen echado el ojo, pero me vendría bien otro par.$b$bVe a Ventormenta si te interesa ayudarnos. El inspector Snip Tuerceganzúas dirige la investigación desde allí, y necesita toda la ayuda que podamos prestarle.', '', '', '', '', '', '', 0);
