INSERT INTO `quest_template_locale` VALUES (25174, 'zhTW', '塔文布萊德必須知道', '到南貧瘠之地的塔文布萊德巡防地向塔文布萊德將軍回報。', '<塔文布萊德將軍會想知道這場怯弱攻擊的細節。你把證據收集了起來。>', '', '', '', '', '', '到南貧瘠之地的塔文布萊德的巡防向塔文布萊德將軍回報。', 0);
INSERT INTO `quest_template_locale` VALUES (25175, 'deDE', 'Schlechte Nachrichten', 'Überbringt General Doppelzopf bei Doppelzopfs Patrouille im Südlichen Brachland die schlechte Nachricht.', '<Sanft schließt Ihr Marleys Augen.>$B$B<Jemand muss General Doppelzopf mitteilen, dass die Horde seinen einzigen Sohn getötet hat.>$B$B<Dieser Jemand seid Ihr.>', '', '', '', '', '', 'Überbringt General Doppelzopf bei Doppelzopfs Patrouille im Südlichen Brachland die schlechte Nachricht.', 0);
INSERT INTO `quest_template_locale` VALUES (25175, 'esES', 'Malas noticias', 'Lleva las malas noticias al general Trenzado en la Patrulla de Trenzado en Los Baldíos del Sur.', '<Cierras suavemente los ojos de Marley.>$b$b<Alguien va a tener que informar al general Trenzado de que la Horda ha matado a su único hijo.>$b$b<Y $gese:esa; eres tú.>', '', '', '', '', '', 'Lleva las malas noticias al General Trenzado en la Patrulla de Trenzado en Los Baldíos del Sur.', 0);
