INSERT INTO `quest_template_locale` VALUES (25488, 'ptBR', 'O Barcódromo', 'Fale com Xabu Latafuso no Barcódromo do Xabu e do Relé em Mil Agulhas.', '$GPronto:Pronta; para partir, $c? Um dos meus sentinelas $Go:a; levará ao Circuito da Ilusão.$B$BPor favor, ajude todos os sobreviventes de lá.$B$BQue a Deusa vele por ti!', '', '', '', '', '', '', 0);
