INSERT INTO `quest_template_locale` VALUES (25489, 'itIT', 'Due, se dal mare', 'Parla con Pozzik alla Turbochiatta di Fizzle e Pozzik nella regione di Millepicchi.', '$GPronto:Pronta; a partire, $C?$B$BUno dei nostri guerrieri ti porterà alla Turbochiatta di Fizzle e Pozzik. Se c\'è qualcuno ancora vivo, avrà un disperato bisogno d\'aiuto.$B$BChe la Madre Terra vegli su di te.', '', '', '', '', '', '', 0);
