INSERT INTO `quest_template_locale` VALUES (25517, 'frFR', 'Baston de bar !', 'Déclenchez une baston de bar !', 'Ces satanés bons à rien de Gobelins puants et verdâtres ont dépassé les bornes !$b$bJe veux que vous fassiez une descente au bar de la péniche de course, que vous commandiez une bouteille de grog, et que vous la fracassiez sur le crâne d’un Gobelin !$b$bMontrez-leur que nous en avons assez de leur insolence, $n !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25517, 'itIT', 'Rissa da taverna!', 'Scatena una rissa da taverna!', 'Quei buoni a nulla puzzolenti dei Goblin se la sono cercata!$B$BVoglio che ti rechi alla Taverna della Turbochiatta, ordini una bottiglia di grog e la spacchi sulla testa di uno di loro!$B$BDevono capire che non tollereremo oltre la loro insolenza, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25517, 'koKR', '주점에서 치고받기!', '주점에서 싸움을 시작해야 합니다!', '모두 저 불경하고, 냄새나고, 뼛속까지 퍼런 고블린들이 자초한 일입니다!$B$B쾌속선 주점으로 가서 그로그주를 주문한 다음, 그 술병으로 저놈 중 한 명의 머리를 때려버리세요!$B$B이들의 오만한 행동을 더는 용납할 수 없다는 걸 보여주자고요, $n 님!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25517, 'ptBR', 'Mais uma briga e outra confusão no bar!', 'Arrume briga no bar!', 'Aqueles goblinzinhos fedorentos, gananciosos e trambiqueiros estão pedindo!$B$BQuero que você vá ao Bar do Barcódromo, peça uma garrafa de grogue e quebre na cabeça de um deles!$B$BMostre a todos eles que não vamos mais tolerar tamanha insolência, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25517, 'ruRU', 'Драка в баре!', 'Начните драку в баре!', 'Ну все, эти вонючие жаблины напросились!$B$BОтправляйся-ка в Бар гоночной баржи, закажи бутылку грога и врежь кому-нибудь из них промеж глаз!$B$BОбъясни им, $n, что больше мы их наглость терпеть не намерены!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25517, 'zhCN', '酒吧斗殴！', '挑起酒吧斗殴！', '那些不干好事、臭气烘烘、青面绿鳃的地精是自找的！$B$B我要你到高速驳船酒吧里去，点一瓶烈酒，砸一个地精头上！$B$B让他们知道我们可不会听任他们放肆，$n！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25517, 'zhTW', '酒吧鬥毆!', '酒吧鬥毆!開打!', '那些差勁的、臭死人的綠皮膚哥布林是自找的!$B$B我要你到高速駁船酒吧去，點一瓶烈酒，然後拿起酒瓶往其中一個傢伙的頭上敲!$B$B讓他們知道我們不會繼續容忍他們的厚顏無禮，$n!', '', '', '', '', '', '', 0);
