INSERT INTO `quest_template_locale` VALUES (25518, 'ruRU', 'Драка в баре!', 'Начните драку в баре!', 'Ну все, эти недотепы-гномы, которых можно за кружку с элем спрятать, напросились!$B$BОтправляйся-ка в Бар гоночной баржи, закажи бутылку грога и врежь кому-нибудь из них промеж глаз!$B$BОбъясни им, $n, что больше мы их высокомерие терпеть не намерены!', '', '', '', '', '', '', 0);
