INSERT INTO `quest_template_locale` VALUES (25531, 'deDE', 'Zwielichtaufstand', 'Sprecht mit Jarod Schattensang an den Toren von Sothann auf dem Hyjal.', 'Ich bin Euch dankbar, dass Ihr mich befreit habt. Lasst uns den Aufruhr, den Ihr verursacht habt, nutzen und hier verschwinden.$B$BWir müssen uns beeilen, bevor die Kultisten uns auf die Schliche kommen.$B$BSucht mich wieder auf, wenn wir diesen Ort verlassen haben.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25531, 'esES', 'Motín Crepuscular', 'Habla con Jarod Cantosombrío en las Puertas de Sothann en Hyjal.', 'Te agradezco que me hayas liberado. Aprovechemos la distracción que has creado y salgamos de aquí.$b$bTenemos que darnos prisa antes de que los cultores se den cuenta.$b$bReúnete conmigo cuando salgamos de este lugar.', '', '', '', '', '', '', 0);
