INSERT INTO `quest_template_locale` VALUES (25586, 'ptBR', 'Silencie os canhões', 'Destrua 8 Canhões das Raízes dos Mares do Sul.', 'Tá na hora de cuidar daqueles malditos canhões lá em cima, nos penhascos do leste!$B$BSe não for pedir muito, volta pro seu barco e vai lá nas Raízes dos Mares do Sul. No alto, você vai ver os canhões que ficam atirando na gente.$B$BAqueles piratas não sabem nada de engenharia. Não deve ser difícil entupir aqueles canhões e fazer eles explodirem.', '', '', '', '', '', '', 0);
