INSERT INTO `quest_template_locale` VALUES (25604, 'deDE', 'Es hat sich ausgehammert', 'Bringt das mysteriöse Gerät nach Sturmwind, Eisenschmiede, Exodar oder Darnassus und zeigt es einem Botschafter des Irdenen Rings.', 'Der Riss, der hier war, hat dieses seltsame Gerät hinterlassen, aber wieso?$B$BVielleicht hat jemand in einer der Hauptstädte mehr Informationen darüber.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25604, 'esES', 'Rómpete el coco', 'Lleva el dispositivo misterioso a Ventormenta, a Forjaz, a El Exodar o a Darnassus y muéstraselo a un representante del Anillo de la Tierra.', 'Este extraño dispositivo apareció en la falla que ocupaba este lugar, ¿pero por qué?$B$BQuizás alguien de una capital pueda ofrecernos más información.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25604, 'esMX', 'Rómpete el coco', 'Lleva el dispositivo misterioso a Ventormenta, a Forjaz, a El Exodar o a Darnassus y muéstraselo a un representante del Anillo de la Tierra.', 'Este extraño dispositivo apareció en la falla que ocupaba este lugar, ¿pero por qué?$B$BQuizás alguien de una capital pueda ofrecernos más información.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25604, 'frFR', 'Le Marteau enfonce le clou', 'Apportez le Mystérieux appareil à Hurlevent, Forgefer, Exodar ou Darnassus et montrez-le à un Représentant du Cercle terrestre.', 'La faille qui s’est formée ici recelait cet étrange appareil. Que faisait-il là et à quoi a-t-il servi ?$b$bVous pourriez en apprendre davantage dans une capitale.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25604, 'itIT', 'Misteriosi dispositivi', 'Porta il Dispositivo Misterioso a Roccavento, Forgiardente, Exodar o Darnassus e mostralo a un Rappresentante del Circolo della Terra.', '<La fenditura che si trovava qui ha lasciato questo strano oggetto... ma perché?>$B$B<Forse qualcuno che vive in una capitale può saperne di più.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25604, 'ptBR', 'A verdade está aí dentro', 'Leve o Dispositivo Misterioso para Ventobravo, Altaforja, Exodar ou Darnassus, e mostre-o para um Representante da Harmonia Telúrica.', 'Você encontra um estranho dispositivo no mesmo lugar onde a fenda elemental estava. O que será isso?$B$BAlgum estudioso de uma das capitais deve saber algo a respeito.', '', '', '', '', '', '', 0);
