INSERT INTO `quest_template_locale` VALUES (25790, 'frFR', 'Retour à la case Pignouf', 'Parlez à Pignouf Fulgurouage, dans les Hauteurs du Sabot-Fendu, dans les Mille pointes.', 'Vous me prenez pour un imbécile ?$B$BAllez retrouver votre maître et dites-lui que nous ne passerons jamais aucun accord ! Enfin, si toutefois vous arrivez en vie jusqu’à lui.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25790, 'itIT', 'Torna da Crazzle', 'Parla con Crazzle Mollapazza nelle Alture di Zoccolo Spezzato a Millepicchi.', 'NON sono stupido!$B$BTorna dal tuo padrone e digli che NON ci sarà nessun accordo! Sempre che ti riesca di sopravvivere e di tornare da lui.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25790, 'koKR', '다시 크래즐에게', '버섯구름 봉우리의 갈래발굽 언덕에 있는 크래즐 스프리스프로켓과 대화해야 합니다.', '내가 바보로 보이나!$B$B네 주인에게 돌아가서 거래는 끝났다고 전해라! 물론, 네가 살아 돌아갔을 때의 이야기이지.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (25790, 'ptBR', 'De volta para o Crazzle', 'Fale com Crazzle Molavivaz na Serra do Casco Fendido, que fica em Mil Agulhas.', 'Eu NÃO SOU IDIOTA!$B$BVolte para o seu senhor e diga que não haverá acordo! Claro, se você conseguir sair daqui $Gvivo:viva;.', '', '', '', '', '', '', 0);
