INSERT INTO `quest_template_locale` VALUES (26360, 'deDE', 'Gedankenkontrolle', 'Erhaltet Braddoks Riesengehirn von Braddok in Zul\'Mamwe.', 'Gedankensicht nicht genug, nein.$B$BGedankenkontrolle viel mächtigere Magie, ja.$B$BMächtige Magie braucht mächtige Reagenzien und Trollhirn ist mächtiges Reagenz.$B$BBraddok in Zul\'Mamwe sehr intelligent, ja.$B$BGeht nach Süden und untersucht Braddoks großes Gehirn.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (26360, 'esES', 'Control mental', 'Obtén el gran cerebro de Braddok de Braddok en Zul\'Mamwe.', 'La Visión mental no es suficiente, no.$b$bEl Control mental es una magia mucho más poderosa, sí.$b$bLa magia poderosa requiere componentes poderosos. El cerebro de trol es uno de ellos.$b$bBraddok, en Zul\'Mamwe, es muy listo, sí.$b$bVe al sur y hazte con el cerebro grandote de Braddok.', '', '', '', '', '', '', 0);
