INSERT INTO `quest_template_locale` VALUES (26494, 'itIT', 'Jasper Mastrintruglio', 'Porta i Campioni di Sassy a Jasper Mastrintruglio nel Rifugio di Serrabullona.', 'Ti dispiace portare un po\' di questa roba a Jasper, nelle suite di lusso? Sono sicura che saprebbe farne buon uso.$b$bGrazie, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (26494, 'koKR', '혼합대가 재스퍼', '하드렌치 은신처에 있는 혼합대가 재스퍼에게 새시의 견본품을 가져가야 합니다.', '호화 특실 근처에 있는 재스퍼에게 이것 좀 전달해 주시겠어요? 재스퍼라면 활용법을 알아낼 수 있을 거예요.$b$b고맙습니다, $n 님!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (26494, 'ptBR', 'M.C. Jasper', 'Leve as Amostras da Espoleta para o M.C. Jasper no Esconderijo da Chaveforte.', 'Você poderia entregar algumas dessas coisas para o Jasper, lá nas suítes superiores? Tenho certeza de que ele vai encontrar uma utilidade perfeita para isso.$b$bObrigada, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (26494, 'ruRU', 'Смешиватель Джаспер', 'Отнесите образцы Хамми смешивателю Джасперу в Укрытие Кофельнагель.', 'Ты не передашь это Джасперу? Он там, у богатых квартир. Ему это очень пригодится.$B$BСпасибо, $n!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (26494, 'zhCN', '混合大师加斯帕', '把莎希的样本交给硬钳避世乐园的混合大师加斯帕。', '介不介意把这东西拿点给加斯帕，就在豪华套间那儿？我肯定他会用的着的。$b$b谢谢啦，$n！', '', '', '', '', '', '', 0);
