INSERT INTO `quest_template_locale` VALUES (26774, 'deDE', 'Gedankenkontrolle', 'Erbeutet Braddoks Riesengehirn von Braddok in Zul\'Mamwe.', 'Gedankensicht nicht genug, nein.$b$bGedankenkontrolle viel mächtigere Magie, ja.$b$bMächtige Magie braucht mächtige Reagenzien und Trollgehirn ist mächtige Reagenz.$b$bBraddok in Zul\'Mamwe, sehr klug, ja.$b$bGeht nach Süden und seht Euch an großes Gehirn von Braddok.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (26774, 'esES', 'Control mental', 'Obtén el gran cerebro de Braddok de Braddok en Zul\'Mamwe.', 'La Visión mental no es suficiente, no.$b$bEl Control mental es una magia mucho más poderosa, sí.$b$bLa magia poderosa requiere componentes poderosos. El cerebro de trol es uno de ellos.$b$bBraddok, en Zul\'Mamwe, es muy listo, sí.$b$bVe al sur y hazte con el cerebro grandote de Braddok.', '', '', '', '', '', '', 0);
