INSERT INTO `quest_template_locale` VALUES (27040, 'frFR', 'Le décryptage pour les nuls', 'Décryptez 6 plans.', 'Nous n’avons pas les outils nécessaires pour déchiffrer ces plans codés. Mais les membres du Marteau du crépuscule les ont, eux.$B$BAuriez-vous la gentillesse d’aller rapporter les plans là-haut et de les faire passer dans leurs décrypteurs à usage unique ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27040, 'itIT', 'Decifrazione facilitata', 'Decifra 6 Piani.', 'Noi non abbiamo gli strumenti per decifrare questi piani codificati, ma il Martello del Crepuscolo sì.$B$BSaresti così gentile da riportare i piani lassù e decifrarli con le loro Macchine di Decifrazione Monouso?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27040, 'koKR', '참 쉬운 해독', '계획서 6장을 해독해야 합니다.', '우리에겐 이 암호화된 계획서를 해독할 도구가 없어요. 하지만 황혼의 망치단에겐 있죠.$B$B이 계획서를 들고 다시 그쪽으로 가서 놈들의 일회용 해독 기계에 넣어 보시겠어요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27040, 'ptBR', 'Decodificação para idiotas', 'Decodifique 6 planos.', 'Não temos as ferramentas para decodificar estes planos, mas o Martelo do Crepúsculo tem.$B$BVocê poderia ser $Gum bom menino:uma boa menina; e levar os planos até lá para rodar em um daqueles antigos dispositivos de decodificação?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27040, 'ruRU', 'Простая дешифровка', 'Расшифруйте 6 планов.', 'У нас нет инструментов, чтобы расшифровать эти планы. А у Сумеречного Молота есть.$b$bНе будешь ли так $gлюбезен:любезна; отнести планы туда и пропустить их через одноразовые дешифровальные устройства?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27040, 'zhCN', '轻松解密', '解密6份计划书。', '我们没有能够解开密文计划书的工具。不过，暮光之锤有。$B$B你能不能做件好事，把计划书带回去，在他们的一次性解密装置里破译一下？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27040, 'zhTW', '簡單解譯法', '解譯6份計畫。', '我們沒有工具去解譯這些加密的計畫。不過，暮光之錘那裡有。$B$B你願意當個好人，把計畫帶回那裡，用他們的拋棄式解密裝置跑一次嗎？', '', '', '', '', '', '', 0);
