INSERT INTO `quest_template_locale` VALUES (27041, 'ruRU', 'Простая дешифровка', 'Расшифруйте 6 планов.', 'У нас нет инструментов, чтобы расшифровать эти планы. А вот у Сумеречного Молота наверняка есть.$b$bНе будешь ли так $gлюбезен:любезна; отнести планы туда и пропустить их через одноразовые дешифровальные устройства?', '', '', '', '', '', '', 0);
