INSERT INTO `quest_template_locale` VALUES (27139, 'deDE', 'Hobart braucht Euch', 'Sprecht mit Hobart Wurfhammer in Instastadt auf den Verlorenen Inseln.', 'He da, Boss! Hobart Wurfhammer möchte mit Euch sprechen. Er sagt, es wäre eine Angelegenheit von höchster Dringlichkeit!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27139, 'esES', 'Hobart te necesita', 'Habla con Hobart Martillazos en la Ciudad de Bolsillo, en Las Islas Perdidas.', 'Eh, $gjefe:jefa;, Hobart Martillazos quiere hablar contigo. Dice que se trata de algo urgente.', '', '', '', '', '', '', 0);
