INSERT INTO `quest_template_locale` VALUES (27273, 'frFR', 'Une invitation pour Reflet-de-Lune', 'Parlez à Loganaar à Havrenuit, à Reflet-de-Lune.', 'Vous venez de rater le messager de Reflet-de-Lune, $n. Il n’a pas dit grand-chose, juste qu’il était porteur d’un message urgent de la part de Loganaar, l’un des entraîneurs les plus respectables du Cercle cénarien. Allez à Reflet-de-Lune dès que possible, $n. La question semblait importante.$b$bVous trouverez Loganaar dans le village de Havrenuit.', '', '', '', '', '', '', 0);
